import React, { useState } from 'react'

const Quantity = ({name,price}) => {
const[Quantitys, setquantity]=useState(0)
  return (
    <div>
      <h2>{name}</h2>
      <p>{price}</p>
       <p>Quantity : {Quantitys}</p>
      <p>total : {Quantitys * price}</p>
      <button onClick={()=> setquantity(Quantitys+1)}>Increace</button>
      <button onClick={()=> Quantitys>0 && setquantity(Quantitys-1)}>Decreace</button>
    </div>
  )
}

export default Quantity
